import './App.css';
import axios from 'axios'
import {useState, useEffect} from 'react'
//URL  https://gateway.marvel.com:443/v1/public/characters?apikey=

// public key e96f74768944fd2e0326f955e87af5c0
// private key 8e7925d165cae75fa0e90f3c73d9add5ed186b98
//TS 1
//18e7925d165cae75fa0e90f3c73d9add5ed186b98e96f74768944fd2e0326f955e87af5c0
//HASH d2bbc6daa044c3046520ad52f082fa64

function App() {

  const [personagens, setPersonagens, eventos, setEventos]=useState([])

  useEffect(() =>{
    axios.get('https://gateway.marvel.com:443/v1/public/characters?ts=1&apikey=e96f74768944fd2e0326f955e87af5c0&hash=d2bbc6daa044c3046520ad52f082fa64').then(res=>{
        setPersonagens(res.data.data.results);
        setEventos(res.data.data.results.events);
        console.log(res.data.data.results);
        console.log(res.data.data.results.events);
    }).catch(error=>console.log(error))


  },[])



  return (
    <div className="App">
      <h1></h1>

      <div class="container">

      {personagens.map(personagem =>(

        <div class="card flex-row flex-wrap" key={personagem.id}>
          <div class="card-header border-0">
                <img src={`${personagem.thumbnail.path}.${personagem.thumbnail.extension}`} style={{width:"48px",height:"48px"}}></img>
                <h4 class="card-title">{personagem.name}</h4>
          </div>
          <div class="card-block px-2">
                <p class="card-text">{personagem.description}</p>

          </div>

          {eventos.map(evento =>(
            <div class="card-block px-2">
                  <p class="card-text">{eventos}</p>

            </div>

          ))}
        <div class="w-100"></div>
      </div>

      ))
      }
      </div>
    </div>
  );
}

export default App;
